mapper.select:
select
  user_id, user_username, user_password, user_nickname, user_creattime, user_gender, user_birth, user_email, user_img_url
from myblog.user


        and user_id = #{userId}


        and user_username = #{userUsername}


        and user_password = #{userPassword}


        and user_nickname = #{userNickname}


        and user_creattime = #{userCreattime}


        and user_gender = #{userGender}


        and user_birth = #{userBirth}


        and user_email = #{userEmail}


        and user_img_url = #{userImgUrl}
